#!/bin/bash

useradd -m hadoopuser
mkdir /home/hadoopuser/.ssh
echo "ssh-rsa  root@HBDO-01" >> /home/hadoopuser/.ssh/authorized_keys
echo "ssh-rsa  root@HBDO-02" >> /home/hadoopuser/.ssh/authorized_keys
echo "ssh-rsa  root@HBDO-03" >> /home/hadoopuser/.ssh/authorized_keys
echo "ssh-rsa  root@HBDO-04" >> /home/hadoopuser/.ssh/authorized_keys
echo "ssh-rsa  root@hbdo-controler" >> /home/hadoopuser/.ssh/authorized_keys
chown -R hadoopuser:hadoopuser /home/hadoopuser/.ssh
chmod 600 /home/hadoopuser/.ssh/authorized_keys
chmod 700 /home/hadoopuser/.ssh/
echo "" >> /etc/sudoers
echo "# hadoopuser - cluster master :))" >> /etc/sudoers
echo "hadoopuser ALL=(ALL) NOPASSWD: ALL" >> /etc/sudoers
